watchers.script:
if ( !warehouse.data || !bins.data || !items.data || !stockReco.data ) {
    console.log("Data sources are not ready, exiting.");
    return; // Safety check
} else {
  pageInitialized.value = true;
}  
const sumOfQuantities = bins.data.reduce((total, currentBin) => {
    // Add the current bin's quantity to the running total
    return total + (currentBin.actual_qty || 0);
}, 0); // Start the total at 0
totalStockValue.value = sumOfQuantities
// const draftCount = stockReco.data.filter(reco => reco.status === 'Draft').length; 
// draftStockReco.value = draftCount
console.log("totalStockValue.value =", totalStockValue.value)

// --- Updated Logic ---
// Ensure stockReco.data is an array before processing
if (Array.isArray(stockReco.data)) {
    const totalReconciliations = stockReco.data.length;

    // 1. Get the top 4 reconciliation records for the short list
    stockRecoShort.value = stockReco.data.slice(0, 4);

    // 2. Calculate the number of remaining records.
    // This value can be used in the UI, for example, to show a "+X more" button.
    const remainingCount = totalReconciliations > 4 ? totalReconciliations - 4 : 0;

    // Assuming you have a reactive variable like 'remainingStockRecoCount' to hold this value.
    // If not, you can adapt this to your framework's state management.
    if (typeof remainingStockRecoCount !== 'undefined') {
        remainingStockRecoCount.value = remainingCount;
    }

    console.log(`Showing ${stockRecoShort.value.length} of ${totalReconciliations} stock reconciliations.`);
    console.log(`There are ${remainingCount} additional records not shown.`);

} else {
    console.error("stockReco.data is not an array. Cannot process.");
    stockRecoShort.value = [];
     if (typeof remainingStockRecoCount !== 'undefined') {
        remainingStockRecoCount.value = 0;
    }
}
